section.programlisting:
@Retention( RetentionPolicy.RUNTIME )
@Target( { ElementType.FIELD, ElementType.METHOD } )	
public @interface MyAnnotation {

	String value();
}

public class Foo {

	private String mBar = "Hello";
	
	@UiLabel( "My Bar" )
	public String getBar() {
	
		return mBar;
	}
}
